	//region Constructors

	//
	// Public Constructor Method VirtualMachine252()
	//
	// Purpose:
	//     Construct a VirtualMachine252 object containing an empty memory.
	//
	// Formals:
	//     none
	//
	// Pre-conditions:
	//     none
	//
	// Post-conditions:
	//     none
	//
	// Returns:
	//     none
	//
	// Worst-case asymptotic runtime:
	//     O(1)
	//
	public VirtualMachine252() {
		// Allow access to VM252 statically
		this.accumulator = 0;
		this.programCounter = 0;
		this.memory = new byte[VM252Utilities.numberOfMemoryBytes];

		// Create breakpoints defaulting to false
		this.breakpoints = new boolean[VM252Utilities.numberOfMemoryBytes];
		Arrays.fill(this.breakpoints, false);

		// Initialize each element of the memory to 0
		Arrays.fill(this.memory, (byte) 0x0);

	}

	//
	// Public Constructor Method VirtualMachine252(String objectFileName)
	//
	// Purpose:
	//     Construct a VirtualMachine252 object containing the memory bytes from the objectFileName's vm252 object code
	//
	// Formals:
	//     objectFileName (in) - String containing the name+location of a vm252 object file
	//
	// Pre-conditions:
	//     objectFileName must be an existing and valid vm252 object file
	//
	// Post-conditions:
	//     none
	//
	// Returns:
	//     none
	//
	// Worst-case asymptotic runtime:
	//     O(1)
	//
	public VirtualMachine252(String objectFileName) {
		// Allow access to VM252 statically
		this.accumulator = 0;
		this.programCounter = 0;
		this.memory = new byte[VM252Utilities.numberOfMemoryBytes];

		// Create breakpoints defaulting to false
		this.breakpoints = new boolean[VM252Utilities.numberOfMemoryBytes];
		Arrays.fill(this.breakpoints, false);

		byte[] program = VM252Utilities.readObjectCodeFromObjectFile(objectFileName);
		if (program != null) {

			// Initialize each element of the memory to 0
			Arrays.fill(this.memory, (byte) 0x0);

			// Copy the program bytes into memory
			System.arraycopy(program, 0, memory, 0, program.length);
		}
	}
